mapper.select:
select
id, dynamic_id, user_id, time, status
from moment_likes


        and id = #{id}


        and dynamic_id = #{dynamicId}


        and user_id = #{userId}


        and time = #{time}


        and status = #{status}

select count(1)
from moment_likes


        and id = #{id}


        and dynamic_id = #{dynamicId}


        and user_id = #{userId}


        and time = #{time}


        and status = #{status}

select *
from moment_likes


        and id = #{id}


        and dynamic_id = #{dynamicId}


        and user_id = #{userId}


        and time = #{time}


        and status = #{status}

mapper.delete:
update moment_likes
set status = 1
where id = #{id}
