mapper.select:
select mu.*
from moment_user mu
where user_id = #{userId}

select mu.*, count(follow.followers_id) follow_num, count(fan.followed_id) fan_num
FROM moment_user mu
left join moment_follow follow on mu.user_id = follow.followers_id and follow.status = 0 and mu.user_status =
#{item.userStatus}
left join moment_follow fan on mu.user_id = fan.followed_id and fan.status = 0 and mu.user_status =
#{item.userStatus}


        and mu.user_id  <>  #{item.userId}


        and mu.user_name like concat('%',#{item.userName},'%')


        and mu.user_gender = #{item.userGender}


        and mu.user_birthday = #{item.userBirthday}


        and mu.user_sign = #{item.userSign}


        and mu.user_date = #{item.userDate}


        and mu.user_status = #{item.userStatus}


GROUP BY mu.user_id
order by fan_num desc

select mu.* FROM moment_user mu
mu.user_status = 'E' and mu.user_id in
#{id}

select mu.*
FROM moment_user mu


        and mu.user_id = #{item.userId}


        and mu.user_name = #{item.userName}


        and mu.user_gender = #{item.userGender}


        and mu.user_birthday = #{item.userBirthday}


        and mu.user_sign = #{item.userSign}


        and mu.user_date = #{item.userDate}


        and mu.user_status = #{item.userStatus}

select count(1)
from moment_user


        and user_id = #{userId}


        and user_name = #{userName}


        and user_gender = #{userGender}


        and user_birthday = #{userBirthday}


        and user_avatar = #{userAvatar}


